import React from 'react';
import Btn from './Btn'
import LoadingOverlay from 'react-loading-overlay';
import './App.css';

var Loader = require('react-loader');

class Main extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      pdfURL: '',
      loaded: false,

    };
    this.handleUploadPDF = this.handleUploadPDF.bind(this);
  }

  componentDidMount() {
    this.setState.loaded=false;
  }

  handleUploadPDF(ev) {
    ev.preventDefault();

    const data = new FormData();
    data.append('file', this.uploadInput.files[0]);
    this.setState({loaded:true});
    fetch('http://localhost:5000/upload', {
      method: 'POST',
      body: data,
    }).then((response) => {
      response.json().then((body) => {
        this.setState({ pdfURL: body});
        console.log(this.state.pdfURL)
      });
      this.setState({loaded:false});
    });
  }

  render() {
    return (
      <div>
        <h1 backgroundcolor='#061844'>Upload do Contrato</h1>
      <form onSubmit={this.handleUploadPDF} >
        <div>
          <input ref={(ref) => { this.uploadInput = ref; }} type="file" />
        </div>
        <br />
        <div>
          <button>Extrair</button>     
        </div>
      </form>
      <Loader loaded={!this.state.loaded} ></Loader>
      </div>
    );
  }
}

export default Main;
